import fetch from 'isomorphic-unfetch';
import Layout from './../components/Layout';

const colorStyle = {
    color: 'white'
}

const Info = (props) => (
    <Layout>
    <h1 style={colorStyle}>{props.region.name}</h1>
    <img src={props.region.flag} />
    <p style={colorStyle}>Langauges: {props.region.languages}</p>
    <p>Population: {props.region.population}</p>
    <p>Currencies: {props.region.currencies}</p>
    </Layout>
)

Info.getInitialProps = async function (context) {
    const { name } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/africa/${name}`)
    const region = await res.json()

    console.log(`Fetched country: ${region.name}`)

    return { region }
}

export default Info;
