import fetch from 'isomorphic-unfetch';
import Layout from './../components/Layout';

const colorStyle = {
    color: 'black'
}

const Info = () => (
    
    <Layout>
    <h1 style={colorStyle}>{region.name}</h1>
    <img src={region.flag} />
    <p style={colorStyle}>Langauges: {region.languages}</p>
    <p style={colorStyle}>Population: {region.population}</p>
    <p style={colorStyle}>Currencies: {region.currencies}</p>
    </Layout>
)

Info.getInitialProps = async function (context) {
    const { name } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/regionalbloc/${name}`)
    const region = await res.json()

    setTimeout(() => {
        console.log(`Fetched country: ${region}`)
    }, 500);
    

    return { region }
}

export default Info;
