import fetch from 'isomorphic-unfetch';
import Layout from './../components/Layout';

const colorStyle = {
    color: 'white'
}

const Info = () => (
    
    <Layout>
    <h1 style={colorStyle}>Info</h1>
    </Layout>
)

Info.getInitialProps = async function(context) {
    const { name } = context.query;
    const res = await fetch(`https://restcountries.eu/rest/v2/regionalbloc/eu/${name}`);
    const country = await res.json()

    console.log(`Fetched country: ${country.name}`)

    return { country }
}

export default Info;
